import streamlit as st
import pandas as pd



corportes = {
    "Sachin": 1234,
    "Aditya": 2341,
    "Souvik": 6968,
    "Ayush": 8787,
    "Pruthvi": 3242
}

leaves_data = {
    "Souvik":  (1, 0, 0, 0, 1),
    "Pruthvi": (1, 0, 0, 1, 1),
    "Sachin":  (0, 0, 1, 1, 1),
    "Ayush":   (0, 0, 0, 1, 1),
    "Aditya":  (1, 0, 0, 0, 0)
}

salary_data = {
    "Souvik":  (65000, 65000, 67000, 67000, 68000, 69000, 69000, 69000, 69000, 69000),
    "Pruthvi": (70000, 70000, 68000, 68000, 67000, 67000, 69000, 69000, 69000, 69000),
    "Ayush":   (66000, 67000, 67000, 67000, 67000, 67000, 67000, 67000, 67000, 69000),
    "Sachin":  (78000, 78000, 78000, 78000, 78000, 78000, 67000, 67000, 66000, 66000),
    "Aditya":  (88000, 87000, 87000, 86000, 85000, 87000, 86000, 87000, 87000, 87000)
}



st.title("MY_corporate_dashboard")
st.header(" Welcome to corporate dashboard sir")
st.image("data:image/webp;base64,")


st.sidebar.header("LOGIN TO MY_CORPORATE DASHBOARD")

username = st.sidebar.text_input("Enter your name sir")
password = st.sidebar.text_input("Enter password", type="password")


if username in corportes:
    try:
        password_int = int(password) if password else None
    except:
        password_int = None

    if password_int == corportes[username]:

        st.success(f"Welcome {username} sir")
        st.toast("Aagye yahan tak")
        st.balloons()

        
        option = st.sidebar.selectbox(
            "Choose option",
            ["Today's Task", "Recent Salaries", "Weekly Leaves", "Analytics Summary"]
        )

        
        if option == "Today's Task":
            st.header(" Chalo dekh hi lo aaj kya karna hai")

            try:
                with open(f"{username}.txt", "r") as f:
                    st.text(f.read())
            except:
                st.error("Bhai tere to koi task file hi nahi hai!")
                st.snow()

       
        elif option == "Recent Salaries":
            st.header(" Salary Analysis krna hai tere ko ab wah")

            data = salary_data[username]

            df = pd.DataFrame({
                "Month": list(range(1, 11)),
                "Salary": data
            })

            view_type = st.radio("Salary kaise dekhna chahta hai:", ["Monthwise", "All Salaries"])

            if view_type == "Monthwise":
                month = st.slider("Select month (1–10)", 1, 10)
                st.write(f"Salary for Month {month}: ₹{data[month - 1]}")

            st.subheader(" Salary Trend")
            st.line_chart(df.set_index("Month"))

            st.dataframe(df)

        elif option == "Weekly Leaves":
            st.header("Ye le dekh le apne kamchori ka natija")

            leaves = leaves_data[username]
            df = pd.DataFrame({
                "Day": ["Mon", "Tue", "Wed", "Thu", "Fri"],
                "Leave": leaves
            })

            st.bar_chart(df.set_index("Day"))
            st.write("1 = Leave | 0 = Present")


        elif option == "Analytics Summary":
            st.header(" Corporate Analytics Summary dekh le bhai")

            sal = salary_data[username]
            leaves = leaves_data[username]

            col1, col2, col3 = st.columns(3)
            col1.metric("Avg Salary", f"₹{sum(sal)//len(sal)}")
            col2.metric("Highest Salary", f"₹{max(sal)}")
            col3.metric("Leaves Taken (Week)", sum(leaves))

            st.subheader("Salary Trend")
            st.line_chart(
                pd.DataFrame({"Month": range(1, 11), "Salary": sal}).set_index("Month")
            )

            st.subheader("Leave Pattern")
            st.bar_chart(
                pd.DataFrame({"Day": ["Mon", "Tue", "Wed", "Thu", "Fri"], "Leave": leaves}).set_index("Day")
            )

    else:
        st.error(" Galat password hai bahi")
else:
    if username:
        st.error("BHAG JA iss comapny se  ka nhi hai tu")
